{
  "name": "圆明园遗址公园景区",
  "province": "北京",
  "history": ["圆明园始建于1709年，是清朝皇家园林之一。它曾被誉为“万园之园”，但在第二次鸦片战争中被英法联军焚毁。"],
  "culture": ["圆明园融合了中国古典园林建筑艺术和西方园林风格，是中国园林史上的瑰宝。"],
  "logistics": ["景区位于北京市海淀区，可通过地铁4号线至圆明园站或乘坐公交到达。"],
  "tickets_hours": ["门票价格为成人10元，学生5元；开放时间为旺季（4月1日至10月31日）6:30-20:00，淡季（11月1日至次年3月31日）7:00-18:00。"],
  "photo_spots": [
    {
      "theme": "遗址",
      "spot": "大水法",
      "note": "这里是圆明园内最具代表性的建筑之一，是拍照留念的好地方。"
    },
    {
      "theme": "自然景观",
      "spot": "福海",
      "note": "福海是圆明园内的大型湖泊，周围景色宜人，适合拍摄湖光山色。"
    }
  ],
  "attractions": [
    {
      "name": "大水法",
      "type": "遗址",
      "desc": "大水法是圆明园内著名的喷泉景观之一。",
      "lat": null,
      "lon": null
    },
    {
      "name": "福海",
      "type": "湖泊",
      "desc": "福海是圆明园内的大型人工湖，周围环境优美。",
      "lat": null,
      "lon": null
    }
  ],
  "routes": [
    {
      "name": "遗址游览路线",
      "duration": "2-3小时",
      "steps": ["从正门进入，首先参观大水法；接着前往福海欣赏湖景；最后可参观西洋楼遗址区。"]
    }
  ],
  "myths": [],
  "geo": {
    "lat": null,
    "lon": null,
    "hint": "圆明园位于北京市海淀区"
  }
}

