{
  "name": "明十三陵景区",
  "province": "北京",
  "history": ["明十三陵是明朝13位皇帝的陵墓群，始建于永乐七年（1409年），历经276年。"],
  "culture": ["作为中国重要的历史文化遗址之一，明十三陵体现了中国古代皇家建筑的独特风格和高超技艺。"],
  "logistics": ["景区位于北京市昌平区天寿山脚下，交通便利，可乘坐公交或自驾前往。"],
  "tickets_hours": ["开放时间一般为8:00-17:00，具体以官方公告为准；门票价格根据季节和政策可能有所调整。"],
  "photo_spots": [
    {"theme": "皇家陵寝", "spot": "长陵祾恩殿", "note": "这里是明成祖朱棣的陵墓，是十三陵中规模最大的一座。"},
    {"theme": "自然风光", "spot": "神路石像生", "note": "这条长达7公里的神道两旁排列着精美的石雕和石刻，展现了明代的艺术成就。"}
  ],
  "attractions": [
    {"name": "长陵", "type": "皇家陵寝", "desc": "明成祖朱棣及其皇后徐氏的合葬墓地，是十三陵中规模最大、保存最完整的陵墓之一。", "lat": null, "lon": null},
    {"name": "定陵地下宫殿", "type": "博物馆", "desc": "展示了明朝皇帝和皇后的棺椁及随葬品，是了解明代皇家文化的重要场所。", "lat": null, "lon": null}
  ],
  "routes": [
    {
      "name": "经典游览路线",
      "duration": "约4小时",
      "steps": ["从入口进入后首先参观神路石像生", "接着前往长陵祾恩殿", "最后可以去定陵地下宫殿了解明代皇家文化"]
    }
  ],
  "myths": ["传说十三陵地宫中藏有大量金银财宝，但至今未被发现。"],
  "geo": {"lat": null, "lon": null, "hint": "具体经纬度信息需进一步核实"}
}

